#!/bin/bash 

mkdir -p dataset/coco/

curl -o --retry 10 --retry-delay 10 coco_train2017.zip -L http://images.cocodataset.org/zips/train2017.zip
unzip coco_train2017.zip 
mv coco_train2017/ dataset/coco/image

curl -o --retry 5 coco_segmap.zip -L https://github.com/HanYangZhao/SPADE-Tensorflow/releases/download/aed20k/coco_segmap.zip
mv segmap dataset/coco/segmap

curl -o --retry 5 coco_val2017.zip -L http://images.cocodataset.org/zips/val2017.zip
unzip coco_val2017.zip
mv coco_val2017/ dataset/coco/val

curl -o --retry 5 coco_segmap_test.zip -L https://github.com/HanYangZhao/SPADE-Tensorflow/releases/download/aed20k/coco_segmap_test.zip
unzip coco_segmap_test.zip
mv segmap_test/ dataset/coco/segmap_test


